import React from 'react'
import { View, Text, StyleSheet, ImageBackground } from 'react-native'
import { connect } from 'react-redux'
import Card from '../../components/card'

const Home = (props) => {
    return (
        <View style={styles.Container}>
            <View style={styles.ContainerNotDrop}>
                <Text style={styles.BemVindo}>
                    Olá,
                </Text>
                
                    {/* {modules.map((item, key) => (
                        <Text style={styles.textUsuario}>
                            {item.usuario}
                        </Text>
                    ))} */}
                
                <ImageBackground source={require('../../../assets/Fundo.png')} style={{ width: "100%", height: "100%", marginLeft: "auto", marginRight: "auto", position: "absolute" }} />

            </View>
            <View style={styles.ContainerDrop}>
                <Text style={styles.textPedidos}>
                    Nossos Planos
                </Text>
                <View style={styles.containerCards}>
                    <Card title="Landing Page" />
                    <Card title="Básico" />
                    <Card title="Completo" />
                </View>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    Container: {
        flex: 1,
        backgroundColor: "purple"
    },
    ContainerNotDrop: {
        flex: 1
    },
    ContainerDrop: {
        flex: 1,
        backgroundColor: "white",
        borderTopLeftRadius: 60,
        borderTopRightRadius: 60
    },
    BemVindo: {
        color: "white",
        fontSize: 50,
        fontWeight: "bold",
        letterSpacing: 0.5,
        marginTop: 80,
        marginLeft: 10
    },
    textUsuario: {
        color: "white",
        fontSize: 30,
        letterSpacing: 0.5,
        marginLeft: 10
    },
    textPedidos: {
        color: "purple",
        fontSize: 35,
        fontWeight: "bold",
        letterSpacing: 0.5,
        textAlign: "center"
    },
    containerCards: {
        padding: 20,
        flex: 1,
        flexDirection: "row",
        justifyContent: "space-around",
        flexWrap: "wrap",
    }
})

const mapStateToProps = ({user}) => {
    return {
        usuario : user.usuario,
        senha: user.senha
    }
}


export default connect(mapStateToProps, null)(Home)
