import React from 'react'
import { Text, View, StyleSheet } from 'react-native'
import Icon from '@expo/vector-icons/FontAwesome5'
import { LinearGradient } from 'expo-linear-gradient';

const Details = props => {
    return (
        <View style={styles.container}>
            <View style={styles.ContainerNotDrop}>
                <Text style={styles.titulo}>
                    <Icon name={
                        props.route.params.title == "Completo"
                            ? "fire"
                            : props.route.params.title == "Personalizado"
                                ? "asterisk"
                                : props.route.params.title == "Básico"
                                    ? "chart-line" : "network-wired"
                    } color="purple"
                        size={40} color="white" /> {props.route.params.title}</Text>
            </View>

            <View style={styles.ContainerDrop}>
                <View style={styles.descricao}>
                    <LinearGradient
                        // Background Linear Gradient
                        colors={['#800080', '#470047']}
                        style={{
                            position: 'absolute',
                            left: 0,
                            right: 0,
                            top: 0,
                            height: 200,
                            borderRadius: 50
                        }}
                    />
                    <Text style={styles.textoDesc}>
                        Plano
                    </Text>
                </View>
            </View>

        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "purple"
    },
    ContainerNotDrop: {
        flex: 1,
        alignItems: "center",
        justifyContent: "center"
    },
    ContainerDrop: {
        flex: 4,
        backgroundColor: "white",
        borderTopLeftRadius: 60,
        borderTopRightRadius: 60,
        alignItems: "center",
        flexDirection: "column",
        padding: 20
    },
    titulo: {
        color: "white",
        fontFamily: "roboto-Bold",
        marginTop: 20,
        fontSize: 40
    },
    descricao: {
        flex: 1,
        padding: 20,
        width: "100%",
        borderRadius: 50
    }
})

export default Details
